import React, { useState } from 'react';
import { addItem, updateItem } from '../firebase/utils'; // Import CRUD functions

// Isolated form component for adding/editing portfolio items
const ItemForm = ({ initialData, setEditingItem }) => {
    const isNew = !initialData.id;
    const [formData, setFormData] = useState({
        title: initialData.title || '',
        description: initialData.description || '',
        embedSrc: initialData.embedSrc || '',
        client: initialData.client || '',
        duration: initialData.duration || '',
        tools: initialData.tools || '',
        year: initialData.year || new Date().getFullYear(),
        type: initialData.type || 'youtube',
    });
    const [status, setStatus] = useState('');

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setStatus('Saving...');
        try {
            if (isNew) {
                await addItem(formData);
                setStatus('Successfully added item!');
            } else {
                await updateItem(initialData.id, formData);
                setStatus('Successfully updated item!');
            }
            
            setTimeout(() => {
                setStatus('');
                setEditingItem(null); // Close the form
            }, 1000); 

        } catch (error) {
            setStatus('Error saving item: ' + error.message);
            console.error(error);
        }
    };

    return (
        <div className="fixed inset-0 bg-gray-900 bg-opacity-95 z-50 flex items-center justify-center p-4">
            <div className="bg-gray-800 p-6 sm:p-8 rounded-xl shadow-2xl max-w-2xl w-full border-2 border-red-500 max-h-screen overflow-y-auto">
                <h2 className="text-2xl font-bold mb-6 text-red-400">{isNew ? 'Add New Portfolio Item' : `Edit: ${initialData.title}`}</h2>
                
                <form onSubmit={handleSubmit} className="space-y-4">
                    
                    {/* Input Fields */}
                    <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
                        <label className="block">
                            <span className="text-gray-400">Title <span className="text-red-500">*</span></span>
                            <input type="text" name="title" value={formData.title} onChange={handleChange} required className="mt-1 block w-full bg-gray-700 border-gray-600 rounded-md p-2 text-white" />
                        </label>
                        <label className="block">
                            <span className="text-gray-400">Client/Brand</span>
                            <input type="text" name="client" value={formData.client} onChange={handleChange} className="mt-1 block w-full bg-gray-700 border-gray-600 rounded-md p-2 text-white" />
                        </label>
                    </div>

                    <label className="block">
                        <span className="text-gray-400">Description</span>
                        <textarea name="description" value={formData.description} onChange={handleChange} rows="3" className="mt-1 block w-full bg-gray-700 border-gray-600 rounded-md p-2 text-white"></textarea>
                    </label>

                    <label className="block">
                        <span className="text-gray-400">Video Source Type</span>
                        <select name="type" value={formData.type} onChange={handleChange} className="mt-1 block w-full bg-gray-700 border-gray-600 rounded-md p-2 text-white">
                            <option value="youtube">YouTube</option>
                            <option value="drive">Google Drive</option>
                        </select>
                    </label>
                    
                    <label className="block">
                        <span className="text-gray-400">Embed URL/Source <span className="text-red-500">*</span></span>
                        <input type="text" name="embedSrc" value={formData.embedSrc} onChange={handleChange} required className="mt-1 block w-full bg-gray-700 border-gray-600 rounded-md p-2 text-white" />
                        <p className="text-xs text-gray-500 mt-1">
                            {formData.type === 'youtube' ? 
                                'Paste the full YouTube URL (e.g., https://www.youtube.com/watch?v=...) or the embed URL.' : 
                                'Paste the Google Drive embed source URL (e.g., https://drive.google.com/file/d/VIDEO_ID/preview).'}
                        </p>
                    </label>
                    
                    <div className="grid grid-cols-3 gap-4">
                        <label className="block">
                            <span className="text-gray-400">Duration</span>
                            <input type="text" name="duration" value={formData.duration} onChange={handleChange} className="mt-1 block w-full bg-gray-700 border-gray-600 rounded-md p-2 text-white" placeholder="e.g., 2:15" />
                        </label>
                        <label className="block">
                            <span className="text-gray-400">Tools</span>
                            <input type="text" name="tools" value={formData.tools} onChange={handleChange} className="mt-1 block w-full bg-gray-700 border-gray-600 rounded-md p-2 text-white" placeholder="e.g., Premiere, Resolve" />
                        </label>
                        <label className="block">
                            <span className="text-gray-400">Year</span>
                            <input type="number" name="year" value={formData.year} onChange={handleChange} className="mt-1 block w-full bg-gray-700 border-gray-600 rounded-md p-2 text-white" />
                        </label>
                    </div>

                    {status && (
                        <div className={`p-3 rounded-md text-center ${status.includes('Error') ? 'bg-red-900 text-red-300' : 'bg-green-800 text-green-300'}`}>
                            {status}
                        </div>
                    )}

                    {/* Actions */}
                    <div className="flex justify-end space-x-4 pt-4">
                        <button 
                            type="button" 
                            onClick={() => setEditingItem(null)} 
                            className="px-4 py-2 bg-gray-600 rounded-md hover:bg-gray-700 transition"
                            disabled={status.includes('Saving')}
                        >
                            Cancel
                        </button>
                        <button 
                            type="submit" 
                            className="px-4 py-2 bg-red-600 rounded-md hover:bg-red-700 transition disabled:opacity-50"
                            disabled={status.includes('Saving') || !formData.title || !formData.embedSrc}
                        >
                            {isNew ? 'Save Video' : 'Update Video'}
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default ItemForm;
