import React, { useState, useEffect } from 'react';
import { savePortfolioItem } from '../firebase/utils';

const initialFormState = {
    title: '',
    description: '',
    videoUrl: '',
    type: 'youtube', // <-- CRITICAL: Initialize type to a valid string
    isHero: false,
};

const ItemForm = ({ initialData, setEditingItem }) => {
    // Merge initialData with initialFormState to ensure all fields are defined
    const [formData, setFormData] = useState({ 
        ...initialFormState, 
        ...initialData 
    });
    const [status, setStatus] = useState('');

    useEffect(() => {
        // Ensure formData is set correctly when initialData changes (e.g., for editing)
        setFormData({ ...initialFormState, ...initialData });
    }, [initialData]);


    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: type === 'checkbox' ? checked : value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setStatus('Saving...');

        // CRITICAL VALIDATION: Check if type is set
        if (!formData.type) {
            setStatus('Error: Video type (YouTube/Drive) is required.');
            return;
        }

        try {
            await savePortfolioItem(formData);
            setStatus('Item saved successfully!');
            // Clear form after successful save
            if (!formData.id) {
                setFormData(initialFormState);
            }
            // If editing, allow user to return to admin panel
            setTimeout(() => setStatus(''), 3000);

        } catch (error) {
            console.error("Failed to save item:", error);
            setStatus(`Failed to save item: ${error.message || error.code}`);
        }
    };

    const isEditing = !!formData.id;

    return (
        <div className="max-w-4xl mx-auto p-4 sm:p-8 bg-gray-800 rounded-xl shadow-2xl border border-red-700 mt-20">
            <h2 className="text-3xl font-extrabold text-white mb-6">
                {isEditing ? 'Edit Portfolio Item' : 'Add New Video'}
            </h2>
            <form onSubmit={handleSubmit} className="space-y-6">
                
                {/* Title and Description */}
                <div>
                    <label htmlFor="title" className="block text-sm font-medium text-gray-300 mb-1">Title</label>
                    <input
                        type="text"
                        name="title"
                        id="title"
                        value={formData.title}
                        onChange={handleChange}
                        required
                        className="w-full p-3 rounded-lg bg-gray-700 text-white border border-gray-600 focus:ring-red-500 focus:border-red-500"
                        placeholder="A title for this awesome project"
                    />
                </div>
                <div>
                    <label htmlFor="description" className="block text-sm font-medium text-gray-300 mb-1">Description</label>
                    <textarea
                        name="description"
                        id="description"
                        value={formData.description}
                        onChange={handleChange}
                        required
                        rows="3"
                        className="w-full p-3 rounded-lg bg-gray-700 text-white border border-gray-600 focus:ring-red-500 focus:border-red-500"
                        placeholder="Explain your role, the tools you used (Premiere Pro, DaVinci), and the project goals."
                    />
                </div>

                {/* Video URL Input */}
                <div>
                    <label htmlFor="videoUrl" className="block text-sm font-medium text-gray-300 mb-1">Video URL (YouTube or Google Drive Embed Link)</label>
                    <input
                        type="url"
                        name="videoUrl"
                        id="videoUrl"
                        value={formData.videoUrl}
                        onChange={handleChange}
                        required
                        className="w-full p-3 rounded-lg bg-gray-700 text-white border border-gray-600 focus:ring-red-500 focus:border-red-500"
                        placeholder="e.g., https://www.youtube.com/watch?v=... OR Google Drive Embed URL"
                    />
                </div>

                {/* Video Type Selector (CRITICAL FIELD) */}
                <div>
                    <label className="block text-sm font-medium text-gray-300 mb-2">Video Source</label>
                    <div className="flex space-x-4">
                        <label className="flex items-center text-white">
                            <input
                                type="radio"
                                name="type"
                                value="youtube"
                                checked={formData.type === 'youtube'}
                                onChange={handleChange}
                                className="form-radio h-4 w-4 text-red-600 border-gray-500 focus:ring-red-500"
                            />
                            <span className="ml-2">YouTube</span>
                        </label>
                        <label className="flex items-center text-white">
                            <input
                                type="radio"
                                name="type"
                                value="drive"
                                checked={formData.type === 'drive'}
                                onChange={handleChange}
                                className="form-radio h-4 w-4 text-red-600 border-gray-500 focus:ring-red-500"
                            />
                            <span className="ml-2">Google Drive</span>
                        </label>
                    </div>
                </div>

                {/* Hero Checkbox */}
                <div className="flex items-center">
                    <input
                        type="checkbox"
                        name="isHero"
                        id="isHero"
                        checked={formData.isHero}
                        onChange={handleChange}
                        className="h-4 w-4 text-red-600 border-gray-600 rounded focus:ring-red-500 bg-gray-700"
                    />
                    <label htmlFor="isHero" className="ml-2 text-sm font-medium text-gray-300">Feature as Hero Showreel (Primary video at the top)</label>
                </div>

                {/* Actions */}
                <div className="flex justify-between items-center pt-4">
                    <button
                        type="submit"
                        className="px-6 py-3 bg-red-600 text-white font-bold rounded-lg shadow-md hover:bg-red-700 transition duration-200"
                        disabled={status === 'Saving...'}
                    >
                        {isEditing ? 'Save Changes' : 'Add Video'}
                    </button>
                    <button
                        type="button"
                        onClick={() => setEditingItem(null)}
                        className="px-4 py-3 bg-gray-600 text-white rounded-lg hover:bg-gray-700 transition duration-200"
                    >
                        Cancel / Back to Admin
                    </button>
                </div>
                
                {status && (
                    <div className={`text-center p-3 rounded-lg ${status.includes('Error') ? 'bg-red-900 text-red-200' : 'bg-green-900 text-green-200'}`}>
                        {status}
                    </div>
                )}
            </form>
        </div>
    );
};

export default ItemForm;
