import React, { useState, useEffect } from 'react';

const LandingAnimation = ({ onAnimationComplete }) => {
    const [exitAnimation, setExitAnimation] = useState(false);

    useEffect(() => {
        // Shuru hobar 3 second por animation shesh hobe
        const timer = setTimeout(() => {
            setExitAnimation(true);
        }, 3000);

        // Shesh hobar 1 second por main page ashbe
        const completeTimer = setTimeout(() => {
            onAnimationComplete();
        }, 4000);

        return () => {
            clearTimeout(timer);
            clearTimeout(completeTimer);
        };
    }, [onAnimationComplete]);

    const name = "[YOUR NAME]"; // Ekhane apnar naam din

    return (
        <div className={`fixed inset-0 bg-black flex items-center justify-center z-[200] transition-opacity duration-1000 ${exitAnimation ? 'opacity-0' : 'opacity-100'}`}>
            <style>
                {`
                .glitch-text {
                    font-family: 'Courier New', Courier, monospace;
                    font-weight: 700;
                    letter-spacing: 0.2rem;
                    position: relative;
                    animation: glitch 2s linear infinite;
                }

                .glitch-text::before,
                .glitch-text::after {
                    content: attr(data-text);
                    position: absolute;
                    top: 0;
                    left: 0;
                    width: 100%;
                    height: 100%;
                    background: black;
                    overflow: hidden;
                }

                .glitch-text::before {
                    left: 2px;
                    text-shadow: -2px 0 red;
                    animation: glitch-before 1.5s linear infinite reverse;
                }

                .glitch-text::after {
                    left: -2px;
                    text-shadow: -2px 0 blue;
                    animation: glitch-after 1.5s linear infinite reverse;
                }

                @keyframes glitch {
                    0% { transform: translate(0); }
                    20% { transform: translate(-3px, 3px); }
                    40% { transform: translate(-3px, -3px); }
                    60% { transform: translate(3px, 3px); }
                    80% { transform: translate(3px, -3px); }
                    100% { transform: translate(0); }
                }

                @keyframes glitch-before {
                    0% { clip-path: inset(85% 0 10% 0); }
                    25% { clip-path: inset(50% 0 25% 0); }
                    50% { clip-path: inset(10% 0 55% 0); }
                    75% { clip-path: inset(70% 0 5% 0); }
                    100% { clip-path: inset(40% 0 30% 0); }
                }

                @keyframes glitch-after {
                    0% { clip-path: inset(15% 0 75% 0); }
                    25% { clip-path: inset(60% 0 15% 0); }
                    50% { clip-path: inset(20% 0 65% 0); }
                    75% { clip-path: inset(80% 0 10% 0); }
                    100% { clip-path: inset(50% 0 20% 0); }
                }

                .loader-line {
                    width: 1px;
                    height: 100%;
                    background: #fff;
                    position: absolute;
                    top: 0;
                    left: 50%;
                    animation: scan 3s linear;
                    opacity: 0.6;
                    box-shadow: 0 0 10px #f00, 0 0 20px #f00;
                }

                @keyframes scan {
                    0% { top: -100%; }
                    100% { top: 100%; }
                }
                `}
            </style>
            
            <div className="loader-line"></div>
            <div className="text-center">
                <h1 
                    className="glitch-text text-2xl md:text-4xl text-white uppercase"
                    data-text={name}
                >
                    {name}
                </h1>
                <p className="text-gray-400 text-sm tracking-widest mt-2">FILMS</p>
            </div>
        </div>
    );
};

export default LandingAnimation;
