import React, { useState, useEffect } from 'react';
import { savePortfolioItem } from '../firebase/utils'; // <-- FIX: Correctly imported savePortfolioItem

const ItemForm = ({ initialData, setEditingItem }) => {
    const isNew = !initialData.id;
    const [formData, setFormData] = useState({
        title: '',
        description: '',
        videoUrl: '', // This will hold the YouTube or Google Drive embed link
        isHero: false,
        ...initialData
    });
    const [loading, setLoading] = useState(false);
    const [statusMessage, setStatusMessage] = useState('');

    useEffect(() => {
        // Reset form data when initialData changes (for toggling between new/edit)
        setFormData({
            title: '',
            description: '',
            videoUrl: '',
            isHero: false,
            ...initialData
        });
    }, [initialData]);

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: type === 'checkbox' ? checked : value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setStatusMessage('');

        try {
            await savePortfolioItem(formData); // <-- FIX: Used correct function name for save/update
            
            setStatusMessage(`Successfully ${isNew ? 'created' : 'updated'} portfolio item!`);
            
            // Clear form and return to admin list view after a successful operation
            setTimeout(() => {
                setEditingItem(null);
            }, 1000);

        } catch (error) {
            console.error("Failed to save item:", error);
            setStatusMessage(`Error: Failed to save item. Check console.`);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="max-w-4xl mx-auto p-4 sm:p-8 bg-gray-800 rounded-xl shadow-2xl mt-8">
            <h2 className="text-3xl font-extrabold text-white mb-6 border-b border-gray-700 pb-3">
                {isNew ? 'Add New Portfolio Item' : 'Edit Item: ' + formData.title}
            </h2>

            <form onSubmit={handleSubmit} className="space-y-6">
                
                {/* Title */}
                <div className="flex flex-col">
                    <label htmlFor="title" className="text-sm font-medium text-gray-300 mb-1">Title</label>
                    <input
                        type="text"
                        name="title"
                        id="title"
                        value={formData.title}
                        onChange={handleChange}
                        required
                        className="p-3 bg-gray-700 border border-gray-600 rounded-lg text-white focus:ring-red-500 focus:border-red-500 transition"
                        placeholder="Project Title (e.g., 'Viral Commercial Ad')"
                    />
                </div>

                {/* Video URL */}
                <div className="flex flex-col">
                    <label htmlFor="videoUrl" className="text-sm font-medium text-gray-300 mb-1">Video Embed/Source URL (YouTube or Google Drive)</label>
                    <input
                        type="url"
                        name="videoUrl"
                        id="videoUrl"
                        value={formData.videoUrl}
                        onChange={handleChange}
                        required
                        className="p-3 bg-gray-700 border border-gray-600 rounded-lg text-white focus:ring-red-500 focus:border-red-500 transition"
                        placeholder="e.g., https://www.youtube.com/embed/... or https://drive.google.com/file/d/.../preview"
                    />
                </div>

                {/* Description */}
                <div className="flex flex-col">
                    <label htmlFor="description" className="text-sm font-medium text-gray-300 mb-1">Description</label>
                    <textarea
                        name="description"
                        id="description"
                        value={formData.description}
                        onChange={handleChange}
                        rows="4"
                        required
                        className="p-3 bg-gray-700 border border-gray-600 rounded-lg text-white focus:ring-red-500 focus:border-red-500 transition"
                        placeholder="Describe your role, the tools used, and the project results."
                    />
                </div>
                
                {/* Is Hero Checkbox */}
                <div className="flex items-center space-x-3">
                    <input
                        type="checkbox"
                        name="isHero"
                        id="isHero"
                        checked={formData.isHero}
                        onChange={handleChange}
                        className="w-4 h-4 text-red-600 bg-gray-700 border-gray-600 rounded focus:ring-red-500"
                    />
                    <label htmlFor="isHero" className="text-sm font-medium text-gray-300">
                        Set as Featured Hero Video (Will display prominently on the main public page)
                    </label>
                </div>

                {/* Status Message */}
                {statusMessage && (
                    <div className={`p-3 text-center rounded-lg font-semibold ${statusMessage.startsWith('Error') ? 'bg-red-900 text-red-300' : 'bg-green-900 text-green-300'}`}>
                        {statusMessage}
                    </div>
                )}

                {/* Action Buttons */}
                <div className="flex justify-end space-x-4 pt-4">
                    <button
                        type="button"
                        onClick={() => setEditingItem(null)}
                        className="px-6 py-2 bg-gray-600 text-white rounded-lg shadow-md hover:bg-gray-500 transition disabled:opacity-50"
                        disabled={loading}
                    >
                        Cancel
                    </button>
                    <button
                        type="submit"
                        className="px-6 py-2 bg-red-600 text-white font-semibold rounded-lg shadow-md hover:bg-red-700 transition disabled:opacity-50 flex items-center justify-center"
                        disabled={loading}
                    >
                        {loading ? (
                            <svg className="animate-spin h-5 w-5 mr-3 text-white" viewBox="0 0 24 24"><circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle><path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path></svg>
                        ) : isNew ? 'Add Item' : 'Save Changes'}
                    </button>
                </div>
            </form>
        </div>
    );
};

export default ItemForm;
