import React from 'react';
import { deletePortfolioItem } from '../firebase/utils'; // <-- FIX: Correctly imported deletePortfolioItem

const AdminPanel = ({ items, setView, setEditingItem }) => {

    const handleDelete = async (itemId, title) => {
        // IMPORTANT: Use a custom modal instead of window.confirm for production
        if (window.confirm(`Are you sure you want to delete the item: "${title}"?`)) {
            try {
                await deletePortfolioItem(itemId);
                console.log(`Item ${itemId} deleted.`);
            } catch (error) {
                console.error("Error deleting document: ", error);
                alert("Failed to delete item. See console for details.");
            }
        }
    };
    
    // Sort items by timestamp (most recent first) for easier admin management
    const sortedItems = items.sort((a, b) => {
        // Handle Firestore server timestamp objects
        const aTime = a.timestamp?.toMillis ? a.timestamp.toMillis() : 0;
        const bTime = b.timestamp?.toMillis ? b.timestamp.toMillis() : 0;
        return bTime - aTime;
    });

    return (
        <div className="min-h-screen bg-gray-900 text-white p-4 sm:p-8">
            <header className="flex flex-col sm:flex-row justify-between items-start sm:items-center border-b border-red-600 pb-4 mb-8">
                <h1 className="text-4xl font-extrabold text-white">Admin Dashboard</h1>
                <div className="flex space-x-3 mt-4 sm:mt-0">
                    <button
                        onClick={() => setEditingItem({})}
                        className="px-4 py-2 bg-red-600 text-white font-semibold rounded-lg shadow-lg hover:bg-red-700 transition transform hover:scale-[1.02] active:scale-100"
                    >
                        + Add New Video
                    </button>
                    <button
                        onClick={() => setView('public')}
                        className="px-4 py-2 bg-gray-700 text-white rounded-lg shadow-lg hover:bg-gray-600 transition"
                    >
                        &larr; View Public Portfolio
                    </button>
                </div>
            </header>

            <p className="text-gray-400 mb-6">
                Manage your portfolio entries below. The item with `isHero` checked will be featured prominently on the public page.
            </p>

            <div className="space-y-4">
                {sortedItems.length === 0 ? (
                    <p className="text-gray-500 text-center py-10">No portfolio items added yet. Click "+ Add New Video" to get started!</p>
                ) : (
                    sortedItems.map((item) => (
                        <div key={item.id} className="flex flex-col md:flex-row items-start md:items-center p-4 bg-gray-800 rounded-xl shadow-lg hover:bg-gray-700 transition">
                            
                            {/* Content */}
                            <div className="flex-grow min-w-0 pr-4">
                                <h2 className="text-xl font-bold text-red-400 truncate">{item.title}</h2>
                                <p className="text-gray-400 text-sm truncate">{item.description}</p>
                                <p className={`text-xs font-mono mt-1 ${item.isHero ? 'text-green-400' : 'text-gray-500'}`}>
                                    {item.isHero ? 'FEATURED (HERO)' : 'STANDARD ITEM'}
                                </p>
                            </div>

                            {/* Actions */}
                            <div className="flex space-x-2 mt-3 md:mt-0 md:ml-auto flex-shrink-0">
                                <button
                                    onClick={() => setEditingItem(item)}
                                    className="px-3 py-1 text-sm bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition shadow-md"
                                >
                                    Edit
                                </button>
                                <button
                                    onClick={() => handleDelete(item.id, item.title)}
                                    className="px-3 py-1 text-sm bg-red-700 text-white rounded-lg hover:bg-red-800 transition shadow-md"
                                >
                                    Delete
                                </button>
                            </div>
                        </div>
                    ))
                )}
            </div>

            <footer className="text-center text-gray-500 text-sm mt-12 pt-6 border-t border-gray-700">
                Admin Panel powered by Firebase Firestore.
            </footer>
        </div>
    );
};

export default AdminPanel;
