import React, { useState, useEffect } from 'react';
import PublicPortfolio from './views/PublicPortfolio';
import AdminPanel from './views/AdminPanel';
import ItemForm from './components/ItemForm';
import {
    auth,
    db,
    signInUser,
    onAuthStateChanged,
    onSnapshot,
    getPortfolioQuery,
    IS_ADMIN_USER,
    signOutUser,
    signInWithEmail
} from './firebase/utils';
import { getAuth } from 'firebase/auth'; 

// Component to handle Admin Login via Email/Password
const AdminLoginModal = ({ closeModal }) => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleLogin = async (e) => {
        e.preventDefault();
        setError('');
        try {
            // Note: signInWithEmail is an async function defined in utils.js
            await signInWithEmail(email, password);
            closeModal();
        } catch (err) {
            console.error("Login failed:", err);
            setError("Login failed. Check email/password and ensure Email/Password Auth is enabled in Firebase.");
        }
    };

    return (
        <div className="fixed inset-0 bg-gray-900 bg-opacity-75 flex items-center justify-center z-[100]">
            <div className="bg-gray-800 p-8 rounded-xl shadow-2xl w-full max-w-md border border-red-700">
                <h2 className="text-2xl font-bold text-white mb-6 text-center">Admin Login</h2>
                <form onSubmit={handleLogin}>
                    <div className="mb-4">
                        <label className="block text-gray-400 text-sm font-bold mb-2" htmlFor="email">Email</label>
                        <input
                            className="shadow appearance-none border border-gray-600 rounded w-full py-3 px-4 text-white bg-gray-700 leading-tight focus:outline-none focus:ring-2 focus:ring-red-500 transition"
                            id="email"
                            type="email"
                            placeholder="admin@example.com"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            required
                        />
                    </div>
                    <div className="mb-6">
                        <label className="block text-gray-400 text-sm font-bold mb-2" htmlFor="password">Password</label>
                        <input
                            className="shadow appearance-none border border-gray-600 rounded w-full py-3 px-4 text-white bg-gray-700 leading-tight focus:outline-none focus:ring-2 focus:ring-red-500 transition"
                            id="password"
                            type="password"
                            placeholder="******************"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                    </div>
                    {error && <p className="text-red-400 text-sm italic mb-4">{error}</p>}
                    <div className="flex items-center justify-between">
                        <button
                            className="bg-red-600 hover:bg-red-700 text-white font-bold py-2 px-4 rounded-lg focus:outline-none focus:shadow-outline transition duration-200"
                            type="submit"
                        >
                            Sign In
                        </button>
                        <button
                            className="bg-gray-600 hover:bg-gray-700 text-white font-bold py-2 px-4 rounded-lg transition duration-200"
                            type="button"
                            onClick={closeModal}
                        >
                            Cancel
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};


const App = () => {
    const [items, setItems] = useState([]);
    const [loading, setLoading] = useState(true);
    const [view, setView] = useState('public'); // 'public' or 'admin'
    const [isAuthReady, setIsAuthReady] = useState(false);
    const [isAdmin, setIsAdmin] = useState(false);
    const [editingItem, setEditingItem] = useState(null); // null for viewing, {} for new, item object for editing
    const [loginModalOpen, setLoginModalOpen] = useState(false);

    // 1. Initialize Firebase and Auth Listener
    useEffect(() => {
        if (!auth) {
            console.error("Firebase Auth is not initialized.");
            setLoading(false);
            return;
        }

        // Attempt to sign in (anonymously or with admin token)
        signInUser();

        const unsubscribe = onAuthStateChanged(auth, (user) => {
            // Check if the app is running on localhost (common scenario for local development)
            const isLocalDev = window.location.hostname === 'localhost' || window.location.hostname === '127.0.0.1';

            if (user) {
                // Check if the user's UID matches the hardcoded admin UID for permanent access
                // NOTE: This UID must match the user you created via email/password in Firebase Auth.
                const permanentAdminUID = 'pwOSgNHvG9Yl8NBM28A66O7ONTP2'; // Your permanent UID
                const isPermanentAdmin = user.uid === permanentAdminUID;

                // GRANT ACCESS: If the user has the secure token OR they are running locally, grant admin access.
                // We also grant access if they are signed in with the permanent email/password account.
                const grantAdminAccess = IS_ADMIN_USER || isLocalDev || isPermanentAdmin;

                // --- START DEBUG LOGGING ---
                console.log("--- APP AUTH STATUS ---");
                console.log("1. User Signed In (UID):", user.uid);
                console.log("2. Is Running Locally (isLocalDev):", isLocalDev);
                console.log("3. Is Permanent Admin (isPermanentAdmin):", isPermanentAdmin);
                console.log("4. FINAL Admin Access Granted (isAdmin):", grantAdminAccess);
                console.log("-------------------------");
                // --- END DEBUG LOGGING ---

                setIsAdmin(grantAdminAccess);
                setIsAuthReady(true);
            } else {
                console.log("Auth State Changed: User is NULL/Unauthenticated. Proceeding as public.");
                // Public (anonymous) user
                setIsAuthReady(true);
            }
            setLoading(false);
        });

        return () => unsubscribe();
    }, []);

    // 2. Real-time Firestore Listener
    useEffect(() => {
        if (!db || !isAuthReady) return;

        const q = getPortfolioQuery();

        const unsubscribe = onSnapshot(q, (snapshot) => {
            const fetchedItems = snapshot.docs.map(doc => ({
                id: doc.id,
                ...doc.data()
            }));

            setItems(fetchedItems);
        }, (error) => {
            console.error("Error fetching portfolio items. This is likely a Firebase Security Rules issue. Please ensure 'allow read' is set for the public path.", error);
        });

        return () => unsubscribe();
    }, [isAuthReady]);

    if (loading) {
        return (
            <div className="min-h-screen bg-gray-900 flex items-center justify-center">
                <div className="text-white text-xl flex items-center">
                    <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-red-500" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24"><circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle><path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path></svg>
                    Loading Application...
                </div>
            </div>
        );
    }

    let content;
    let debugBanner = null;

    const currentAuth = getAuth();
    const isLoggedIn = currentAuth.currentUser && !currentAuth.currentUser.isAnonymous; // Check if logged in with permanent account
    const userDisplayName = isLoggedIn ? currentAuth.currentUser.email : 'Public';

    if (isAdmin) {
        // Visual indicator that Admin Mode is active
        debugBanner = (
            <div className="fixed top-0 left-0 right-0 bg-red-800 text-white text-xs text-center py-1 z-50">
                ADMIN MODE ACTIVE (Signed in as: {userDisplayName}). Host: {window.location.hostname}.
            </div>
        );
    }

    // Admin View Logic
    if (view === 'admin' && isAdmin) {
        if (editingItem !== null) {
            content = <ItemForm initialData={editingItem} setEditingItem={setEditingItem} />;
        } else {
            // Apply padding to avoid being hidden by the fixed debug banner
            content = (
                <div className="pt-8 sm:pt-4">
                    <AdminPanel items={items} setView={setView} setEditingItem={setEditingItem} />
                </div>
            );
        }
    }
    // Unauthorized Admin Access
    else if (view === 'admin' && !isAdmin) {
        content = (
            <div className="min-h-screen bg-gray-900 flex flex-col items-center justify-center p-8 text-center">
                <h1 className="text-4xl font-bold text-red-500 mb-4">Access Denied</h1>
                <p className="text-gray-400 mb-8">You must be logged in as an administrator to view this panel.</p>
                <button
                    onClick={() => setView('public')}
                    className="px-6 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 transition"
                >
                    Go to Public Portfolio
                </button>
            </div>
        );
    }
    // Public View Logic (Default)
    else {
        content = <PublicPortfolio items={items} setView={setView} isAdmin={isAdmin} />;
    }

    return (
        <>
            {debugBanner}
            <nav className={`fixed top-0 left-0 right-0 flex justify-between items-center p-4 text-white bg-gray-900 shadow-lg z-40 ${isAdmin ? 'pt-8 sm:pt-4' : ''}`}>
                <h1 className="text-xl font-extrabold text-red-500">Video Editor Portfolio</h1>
                <div className="flex space-x-4">
                    {isAdmin && view !== 'admin' && (
                        <button
                            onClick={() => setView('admin')}
                            className="px-3 py-1 bg-red-600 rounded-lg hover:bg-red-700 transition duration-200"
                        >
                            Admin Panel
                        </button>
                    )}
                    {isAdmin && view === 'admin' && (
                        <button
                            onClick={() => setView('public')}
                            className="px-3 py-1 bg-gray-600 rounded-lg hover:bg-gray-700 transition duration-200"
                        >
                            &larr; View Public Portfolio
                        </button>
                    )}

                    {isLoggedIn ? (
                        <button
                            onClick={signOutUser}
                            className="px-3 py-1 bg-gray-700 rounded-lg hover:bg-red-500 transition duration-200"
                        >
                            Sign Out
                        </button>
                    ) : (
                        <button
                            onClick={() => setLoginModalOpen(true)}
                            className="px-3 py-1 bg-red-500 rounded-lg hover:bg-red-600 transition duration-200"
                        >
                            Admin Login
                        </button>
                    )}
                </div>
            </nav>
            {content}
            {loginModalOpen && <AdminLoginModal closeModal={() => setLoginModalOpen(false)} />}
        </>
    );
};

// CRITICAL FIX: The export line must be present for index.js to import App.
export default App;
