import React from 'react';
import { deleteItem } from '../firebase/utils'; // Import delete utility

const AdminPanel = ({ items, setView, setEditingItem }) => {
    
    const handleDelete = async (itemId, title) => {
        // Use a custom modal in production, but alert() for simplicity here.
        if (window.confirm(`Are you sure you want to delete "${title}"? This action cannot be undone.`)) {
            try {
                await deleteItem(itemId);
                // Notification logic can be added here
            } catch (error) {
                console.error("Failed to delete item:", error);
                alert("Error deleting item. Check permissions or network.");
            }
        }
    };

    return (
        <div className="min-h-screen bg-gray-900 text-gray-100 p-4 sm:p-8">
            <header className="flex justify-between items-center pb-6 border-b border-red-700 mb-8">
                <h1 className="text-3xl font-bold text-red-500">Admin Panel</h1>
                <div className="space-x-4">
                    <button 
                        onClick={() => setView('public')} 
                        className="px-4 py-2 bg-gray-700 text-white rounded-lg hover:bg-gray-600 transition"
                    >
                        &larr; View Public Portfolio
                    </button>
                    <button 
                        onClick={() => setEditingItem({})} // Start new item creation
                        className="px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 transition"
                    >
                        + Add New Video
                    </button>
                </div>
            </header>

            <h2 className="text-xl font-semibold mb-4 text-gray-300">Current Portfolio Items ({items.length})</h2>
            <p className="text-sm text-gray-500 mb-6">
                The first item in the list automatically serves as the **Hero Showreel** on the public page.
            </p>

            <div className="space-y-4">
                {items.length === 0 && (
                    <div className="text-center p-10 bg-gray-800/70 rounded-lg">
                        <p className="text-gray-500 italic">No items yet. Click "+ Add New Video" to get started.</p>
                    </div>
                )}
                {items.map((item, index) => (
                    <div key={item.id} className="bg-gray-800 p-4 rounded-lg flex flex-col md:flex-row justify-between items-start md:items-center shadow-lg">
                        <div className="flex-1 min-w-0 pr-4">
                            <p className="text-xs font-mono text-gray-500 mb-1">
                                {index === 0 ? <span className="text-red-500 font-bold">[SHOWREEL]</span> : <span className="text-gray-400">Project {index}</span>}
                            </p>
                            <h3 className="text-lg font-bold truncate text-white">{item.title}</h3>
                            <p className="text-sm text-gray-400">{item.client} &middot; {item.year}</p>
                        </div>
                        <div className="flex space-x-2 mt-3 md:mt-0">
                            <button 
                                onClick={() => setEditingItem(item)}
                                className="px-3 py-1 text-sm bg-blue-600 rounded-md hover:bg-blue-700 transition"
                            >
                                Edit
                            </button>
                            <button 
                                onClick={() => handleDelete(item.id, item.title)}
                                className="px-3 py-1 text-sm bg-red-700 rounded-md hover:bg-red-800 transition"
                            >
                                Delete
                            </button>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default AdminPanel;
