import React, { useState, useEffect } from 'react';
import VideoEmbed from '../components/VideoEmbed';

const PublicPortfolio = ({ items, setView, isAdmin, setLoginModalOpen, isLoggedIn, signOutUser }) => {
    // Sort items by creation date to ensure the first item added is always the hero
    const sortedItems = [...items].sort((a, b) => (a.createdAt?.seconds || 0) - (b.createdAt?.seconds || 0));

    const heroItem = sortedItems.length > 0 ? sortedItems[0] : null;
    const projectItems = sortedItems.slice(1);

    const [activeSection, setActiveSection] = useState('hero');

    useEffect(() => {
        const sections = ['hero', 'work', 'contact']; // Removed 'about'
        // This observer detects which section is in the middle of the viewport
        const observer = new IntersectionObserver(
            (entries) => {
                entries.forEach((entry) => {
                    if (entry.isIntersecting) {
                        setActiveSection(entry.target.id);
                    }
                });
            },
            {
                rootMargin: '-50% 0px -50% 0px', // Watches for the vertical center of the screen
                threshold: 0,
            }
        );

        sections.forEach((id) => {
            const el = document.getElementById(id);
            if (el) observer.observe(el);
        });

        return () => sections.forEach((id) => {
            const el = document.getElementById(id);
            if (el) observer.unobserve(el);
        });
    }, []);

    const getNavLinkClass = (section) => {
        const baseClass = "flex items-center justify-center gap-2 px-4 py-2 rounded-full text-sm font-medium transition-all duration-300 transform active:scale-95";
        if (activeSection === section) {
            return `${baseClass} bg-violet-600 text-white shadow-lg`;
        }
        return `${baseClass} text-gray-300 hover:bg-white/10 hover:text-white`;
    };

    const navLinks = [
        { id: 'hero', label: 'Home', icon: <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor"><path d="M10.707 2.293a1 1 0 00-1.414 0l-7 7a1 1 0 001.414 1.414L4 10.414V17a1 1 0 001 1h2a1 1 0 001-1v-2a1 1 0 011-1h2a1 1 0 011 1v2a1 1 0 001 1h2a1 1 0 001-1v-6.586l.293.293a1 1 0 001.414-1.414l-7-7z" /></svg> },
        { id: 'work', label: 'Work', icon: <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor"><path d="M7 3a1 1 0 000 2h6a1 1 0 100-2H7zM4 7a1 1 0 011-1h10a1 1 0 110 2H5a1 1 0 01-1-1zM2 11a1 1 0 011-1h14a1 1 0 110 2H3a1 1 0 01-1-1z" /></svg> },
        { id: 'contact', label: 'Contact', icon: <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor"><path d="M2.003 5.884L10 9.882l7.997-3.998A2 2 0 0016 4H4a2 2 0 00-1.997 1.884z" /><path d="M18 8.118l-8 4-8-4V14a2 2 0 002 2h12a2 2 0 002-2V8.118z" /></svg> },
    ];

    return (
        <div className="bg-gray-900 text-white min-h-screen">
            {/* Final Navbar with robust mobile and desktop positioning */}
            <nav className="fixed bottom-4 md:top-4 left-1/2 -translate-x-1/2 z-50">
                <div className="flex items-center gap-2 bg-black/30 backdrop-blur-lg border border-white/10 rounded-full p-2 shadow-2xl">
                    {navLinks.map(link => (
                        <a key={link.id} href={`#${link.id}`} className={getNavLinkClass(link.id)}>
                            {link.icon}
                            <span className={activeSection === link.id ? 'inline' : 'hidden md:inline'}>{link.label}</span>
                        </a>
                    ))}
                    {isAdmin && (
                         <button onClick={() => setView('admin')} className={`${getNavLinkClass('admin')} bg-red-600/50 hover:bg-red-600/80`}>
                            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="currentColor" viewBox="0 0 20 20"><path d="M17.5,2.5h-15C1.125,2.5,0,3.625,0,5v10c0,1.375,1.125,2.5,2.5,2.5h15c1.375,0,2.5-1.125,2.5-2.5V5C20,3.625,18.875,2.5,17.5,2.5z M10,12.5c-1.375,0-2.5-1.125-2.5-2.5S8.625,7.5,10,7.5s2.5,1.125,2.5,2.5S11.375,12.5,10,12.5z"/></svg>
                            <span className="hidden md:inline">Admin</span>
                        </button>
                    )}
                    {isLoggedIn ? (
                        <button onClick={signOutUser} className={`${getNavLinkClass('logout')} bg-gray-600/50 hover:bg-gray-600/80`}>
                             <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="currentColor" viewBox="0 0 20 20"><path fillRule="evenodd" d="M3 3a1 1 0 00-1 1v12a1 1 0 102 0V4a1 1 0 00-1-1zm10.293 9.293a1 1 0 001.414 1.414l3-3a1 1 0 000-1.414l-3-3a1 1 0 10-1.414 1.414L14.586 9H7a1 1 0 100 2h7.586l-1.293 1.293z" clipRule="evenodd" /></svg>
                            <span className="hidden md:inline">Sign Out</span>
                        </button>
                    ) : (
                        <button onClick={() => setLoginModalOpen(true)} className={`${getNavLinkClass('login')} bg-blue-600/50 hover:bg-blue-600/80`}>
                            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="currentColor" viewBox="0 0 20 20"><path fillRule="evenodd" d="M10 9a3 3 0 100-6 3 3 0 000 6zm-7 9a7 7 0 1114 0H3z" clipRule="evenodd" /></svg>
                            <span className="hidden md:inline">Login</span>
                        </button>
                    )}
                </div>
            </nav>

            {/* 1. Hero Section (Showcase Reel) */}
            <section id="hero" className="relative text-center py-20 md:py-32 min-h-[80vh] flex items-center justify-center overflow-hidden">
                 <div className="absolute inset-0 bg-gray-900 z-10 opacity-60"></div>
                <div className="absolute inset-0 z-0">
                    {/* Decorative background elements */}
                    <div className="absolute top-1/4 left-1/4 w-96 h-96 bg-red-900 rounded-full mix-blend-screen filter blur-3xl opacity-20 animate-blob"></div>
                    <div className="absolute bottom-1/4 right-1/4 w-96 h-96 bg-blue-900 rounded-full mix-blend-screen filter blur-3xl opacity-20 animate-blob animation-delay-4000"></div>
                </div>

                <div className="relative z-20 max-w-4xl mx-auto px-4">
                    <h1 className="text-5xl md:text-7xl font-extrabold mb-4 leading-tight text-white drop-shadow-lg animate-fade-in-down">
                        The Storyteller, <span className="bg-clip-text text-transparent bg-gradient-to-r from-red-500 to-rose-400">The Editor.</span>
                    </h1>
                    <p className="text-xl md:text-2xl text-gray-300 mb-10 drop-shadow-md animate-fade-in-down animation-delay-300">
                        Crafting compelling narratives from raw footage. I make videos *move*.
                    </p>

                    {heroItem ? (
                        <div className="w-full max-w-4xl mx-auto rounded-xl overflow-hidden mb-12 smart-shadow animate-fade-in-up animation-delay-600">
                            <VideoEmbed item={heroItem} />
                        </div>
                    ) : (
                        <div className="aspect-video w-full max-w-4xl mx-auto bg-gray-800/50 border border-gray-700 flex items-center justify-center rounded-xl mb-12 animate-fade-in-up animation-delay-600">
                            <p className="text-gray-500 italic">No Showreel Set. Add items in the Admin Panel.</p>
                        </div>
                    )}
                    
                    <a href="#work" className="inline-flex items-center justify-center px-8 py-4 text-base font-bold rounded-full text-white bg-gradient-to-r from-red-600 to-rose-500 hover:from-red-700 hover:to-rose-600 transition-all duration-300 transform hover:scale-105 shadow-lg animate-fade-in-up animation-delay-900">
                        View My Work
                    </a>
                </div>
            </section>

            {/* Divider */}
            <div className="max-w-6xl mx-auto border-t border-gray-800"></div>

            {/* Work Section - Corrected gallery with click-through fix */}
            <section id="work" className="py-16 md:py-24 bg-gray-800/20">
                <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                    <h2 className="text-4xl font-bold text-center mb-12 bg-clip-text text-transparent bg-gradient-to-r from-red-500 to-rose-400">
                        Selected Work
                    </h2>
                    <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-2 md:gap-4">
                        {projectItems.length > 0 ? (
                            projectItems.map((item) => (
                                <div key={item.id} className="group relative cursor-pointer rounded-lg overflow-hidden smart-shadow">
                                    {/* Video will scale on hover */}
                                    <div className="aspect-video w-full transition-all duration-300 transform group-hover:scale-110">
                                        <VideoEmbed item={item} />
                                    </div>
                                    {/* Overlay for title - CRITICAL FIX: pointer-events-none allows clicks to pass through */}
                                    <div className="absolute inset-0 bg-gradient-to-t from-black/80 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300 flex items-end p-4 pointer-events-none">
                                        <h3 className="text-white text-sm font-bold transform opacity-0 translate-y-4 group-hover:opacity-100 group-hover:translate-y-0 transition-all duration-300 delay-100">{item.title}</h3>
                                    </div>
                                </div>
                            ))
                        ) : (
                            <div className="col-span-2 md:col-span-3 lg:col-span-4 text-center p-10 bg-gray-800/70 rounded-lg">
                                <p className="text-gray-500 italic">No additional projects found. Add more in the Admin Panel!</p>
                            </div>
                        )}
                    </div>
                </div>
            </section>

            {/* Divider */}
            <div className="max-w-6xl mx-auto border-t border-gray-800"></div>

            {/* Contact Section */}
            <section id="contact" className="py-16 md:py-24">
                <div className="max-w-xl mx-auto px-4 text-center">
                    <h2 className="text-4xl font-bold mb-6 bg-clip-text text-transparent bg-gradient-to-r from-red-500 to-rose-400">
                        Let's Create Together
                    </h2>
                    <p className="text-gray-400 text-lg mb-8">
                        Ready to bring your vision to life? I'm available for freelance projects.
                    </p>
                    <a href="mailto:your.email@example.com" className="inline-block text-xl font-medium text-white bg-red-600 px-10 py-4 rounded-lg hover:bg-red-700 transition-all duration-300 transform hover:scale-105 smart-shadow">
                        your.email@example.com
                    </a>
                </div>
            </section>

            {/* Footer */}
            <footer className="bg-gray-900 border-t border-gray-800 mt-12 py-6">
                <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center text-gray-500 text-sm">
                    <p>&copy; {new Date().getFullYear()} [Your Name] Films. All Rights Reserved.</p>
                </div>
            </footer>
        </div>
    );
};

export default PublicPortfolio;
