import React from 'react';

// Isolated component for rendering YouTube or Google Drive embeds
const VideoEmbed = ({ item }) => {
    // For YouTube embeds, format the URL correctly
    const youtubeSrc = item.embedSrc.includes('youtube.com/embed') 
        ? item.embedSrc 
        : `https://www.youtube.com/embed/${item.embedSrc.split('v=')[1]?.split('&')[0]}`;
        
    // For Google Drive embeds, ensure it uses the /preview path
    const driveSrc = item.embedSrc.includes('drive.google.com/file/') 
        ? item.embedSrc.replace('/view', '/preview') 
        : item.embedSrc;

    const finalSrc = item.type === 'youtube' ? youtubeSrc : driveSrc;

    return (
        <div className="aspect-video card-shadow rounded-lg overflow-hidden">
            <iframe 
                className="w-full h-full" 
                src={finalSrc}
                title={item.title} 
                frameBorder="0" 
                allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share" 
                allowFullScreen
                loading="lazy">
            </iframe>
        </div>
    );
};

export default VideoEmbed;
